// import { Form } from "../../Form";
import { StyledLoginButton } from "../../Button";
import { InputAndLabel } from "../../Input";
import { StyledBoxModal } from "./style";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { schemaUserLogin } from "../../../Services/validation/createUser.validation";
import { UserContext } from "../../../Providers/UserProvider";
import { useContext } from "react";
import { FieldError } from "react-hook-form";
import { StyledForm } from "../../Form/styled";
import { IoIosCloseCircleOutline } from "react-icons/io";
import { ProjectsContext } from "../../../Providers/ProjectsProvider";

interface IUserLogin {
    email: string;
    password: string;
    error?: {
        email?: FieldError;
        password?: FieldError;
    };
}
export const ModalLogin = () => {
    const { onSubmitLogin } = useContext(UserContext);
    const { handleModal } = useContext(ProjectsContext);
    const {
        handleSubmit,
        register,
        formState: {
            errors: { email, password },
        },
    } = useForm<IUserLogin>({
        resolver: yupResolver(schemaUserLogin),
    });

    return (
        <StyledBoxModal>
            <StyledForm onSubmit={handleSubmit(onSubmitLogin)}>
                <IoIosCloseCircleOutline
                    onClick={handleModal}
                    className="close"
                />
                <h2>JÃ¡ faz parte?</h2>

                <InputAndLabel
                    textLabel="Email"
                    name="email"
                    placeholder="Digite seu email"
                    type="text"
                    register={register}
                    error={email?.message}
                />
                <InputAndLabel
                    textLabel="Senha"
                    name="password"
                    placeholder="Digite sua senha"
                    type="password"
                    error={password?.message}
                    register={register}
                />
                <StyledLoginButton type="submit">Login</StyledLoginButton>
            </StyledForm>
        </StyledBoxModal>
    );
};
