import styled from "styled-components";

export const StyledInput = styled.div`
    width: 95%;
    height: 60px;

    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;

    input {
        width: 90%;
        height: 0px;

        border: solid 1px var(--rose-700);
    }
`;
