import styled from "styled-components";

export const StyledForm = styled.form`
    width: 95%;
    max-width: 400px;

    border-top: 4px var(--rose-700);
    border-bottom: 4px var(--rose-700);

    padding: 0px 10px;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    h2 {
        font-size: 1.5rem;
        font-weight: 600;
    }
`;
